import React from "react";

class ListItem extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {};
  }

  renderItems = () => {
    return <div></div>;
  };

  handleDragStart = (e, index) => {
    e.target.style.opacity = "0.4";
    this.props.updateDragStart({ parent: this.props.title, index });
  };

  handleDragOver = (e) => {
    this.props.updateDragState({
      start: this.props.dragState.start,
      current: this.props.title,
      end: this.props.dragState.end,
    });
  };

  handleDragEnd = (e) => {
    e.target.style.opacity = "1";
    this.props.updateDragState({
      start: this.props.dragState.start,
      current: this.props.dragState.current,
      end: this.props.title,
    });
    console.log(this.props.dragState);
  };

  render() {
    return (
      <div className="listItem__container">
        <div className="heading">
          <span className="title">{this.props.title}</span>
        </div>
        <div className="data__container" onDragOver={this.handleDragOver}>
          <div
            style={{ padding: "20px" }}
            draggable
            onDragStart={this.handleDragStart}
            onDragEnd={this.handleDragEnd}
          >
            {this.props.title}
          </div>
        </div>
      </div>
    );
  }
}

export default ListItem;
