import React from "react";
import StorageUtils from "../libs/storageUtils";
import { listCount } from "../constant";
import ListItem from "./ListItem";

class List extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = { list: this.getInitialData(listCount, {}) };
    this.i = 0;
    this.dragState = {
      start: null,
      current: null,
      end: null,
    };

    this.cursorPosition = null;
  }

  updateCursorPosition = (position) => {
    this.cursorPosition = position;
  };

  updateDragStart = (start) => {
    this.dragState = {
      start,
      current: start.parent,
      end: null,
    };
  };

  updateDragCurrent = (current) => {
    this.dragState.current = current;
  };

  updateDragEnd = (end) => {
    this.dragState.end = end;
    console.log(this.dragState, this.cursorPosition);
  };

  getInitialData = (key, defaultValue) => {
    const data = StorageUtils.getItem(key);
    console.log(data);
    if (data) {
      return JSON.parse(data);
    }
    StorageUtils.setItem(key, defaultValue);
    return defaultValue;
  };

  addList = () => {
    const newList = { ...this.state.list, ["data" + this.i++]: [] };
    this.setState({ list: newList }, () => {
      StorageUtils.setItem(listCount, newList);
    });
  };

  renderList = () => {
    console.log(this.state.list);
    const dataArray = Object.entries(this.state.list);
    return dataArray.map(([title, data]) => {
      return (
        <ListItem
          title={title}
          data={data}
          dragState={this.dragState}
          updateDragStart={this.updateDragStart}
          updateDragEnd={this.updateDragEnd}
          updateDragCurrent={this.updateDragCurrent}
          updateCursorPosition={this.updateCursorPosition}
        />
      );
    });
  };

  render() {
    return (
      <div className="list__wrapper" onDragOver={(e) => e.preventDefault()}>
        <div className="button__container">
          <button type="button" className="button" onClick={this.addList}>
            Add list
          </button>
        </div>

        <div className="list__container">{this.renderList()}</div>
      </div>
    );
  }
}

export default List;
